(()=>{const e=document.querySelectorAll(".question"),t=document.querySelectorAll(".answer");t.forEach(e=>{e.addEventListener("dragover",e=>{e.dataTransfer.types.includes("question")&&e.preventDefault()})}),console.log("questions",e),console.log("answers",t),e.forEach(e=>{e.addEventListener("dragstart",t=>{e.id="dragged-question",t.dataTransfer.effectAllowed="move",t.dataTransfer.setData("question",""),console.log("Dragging a question")}),e.addEventListener("dragend",t=>{e.removeAttribute("id")})}),t.forEach(e=>{e.addEventListener("drop",e=>{e.preventDefault();const t=document.getElementById("dragged-question");console.log("DraggedTask",t)})})})();