(()=>{const e=document.querySelectorAll(".task-column");e.forEach(e=>{e.addEventListener("dragover",e=>{e.dataTransfer.types.includes("task")&&e.preventDefault()})}),document.querySelectorAll(".task").forEach(e=>{e.addEventListener("dragstart",t=>{e.id="dragged-task",t.dataTransfer.effectAllowed="move",t.dataTransfer.setData("task","")}),e.addEventListener("dragend",t=>{e.removeAttribute("id")})}),e.forEach(e=>{e.addEventListener("drop",t=>{t.preventDefault();const a=document.getElementById("dragged-task");a.remove(),e.children[1].appendChild(a)})})})();